export default {
    add(a, b) {
        return a.map((x, i) => x + b[i])
    },
    sub(a, b) {
        return a.map((x, i) => x - b[i])
    },
    multeach(a, b) {
        return a.map((x, i) => x * b[i])
    },
    mult(a, b) {
        return a.map(x => x * b);
    },
    magsq(a) {
        return a[0] * a[0] + a[1] * a[1];
    },
    max(a, b) {
        return a.map((x, i) => Math.max(x, b[i]));
    },
    min(a, b) {
        return a.map((x, i) => Math.min(x, b[i]));
    }
}